import { GET_CUSTOMER } from "../types";
const initialState = { customerList: [] };

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case GET_CUSTOMER:
      return [...state, payload];

    default:
      return state;
  }
}
