import React, { useEffect } from "react";
import "./App.scss";
import "bootstrap/dist/css/bootstrap.min.css";
import { connect } from "react-redux";
import { getCustomers } from "./action/getCustomer";
import CustomerCard from "./components/CustomerCard";

const App = ({ customerList, getCustomers }) => {
  useEffect(() => {
    getCustomers();
  }, [getCustomers]);

  return (
    <div className="container">
      <h3>Welcome To WDS Insurance</h3>
      <p>Click A Customer's Info Card to Edit its information</p>
      {customerList.map(customer => {
        return <CustomerCard key={customer.id} customer={customer} />;
      })}
    </div>
  );
};

const mapStateToProps = state => ({
  customerList: state.customerList
});

export default connect(mapStateToProps, { getCustomers })(App);
