import { GET_CUSTOMER } from "../actions/types";

const initialState = { list: ["hello", "hi,", "goodmorning"] };

const customerReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CUSTOMER:
      return { ...state, list: action.payload };

    default:
      return state;
  }
};

export default customerReducer;
