import { GET_CUSTOMER } from "../types";
const initialState = { customerList: ["empty"] };

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case GET_CUSTOMER:
      return { ...state, customerList: payload };

    default:
      return state;
  }
}
