import { FETCH_CUSTOMER_LIST } from "../actions/types";

const initialState = [];

const customerlistReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_CUSTOMER_LIST:
      return [...state, action.payload];

    default:
      return state;
  }
};

export default customerlistReducer;
