// export default fetchCustomerList;

export const fetchCustomerList = () => async dispatch => {
  try {
    const res = await axios.get("http://localhost:8080/api/customer");
    dispatch({
      type: FETCH_CUSTOMER_LIST,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: "ERROR",
      payload: err
    });
  }
};
