import React, { useState } from "react";
import { Link } from "react-router-dom";

const CustomerForm = ({ customerFormData }) => {
  const [formData, setFormData] = useState({
    firstName: customerFormData.firstName,
    lastName: customerFormData.lastName,
    address: customerFormData.address,
    gender: customerFormData.gender,
    marital_status: customerFormData.marital_status
  });

  const onChange = event => {
    const val = event.target.val;
    setFormData({ ...formData, [event.target.name]: val });
  };

  console.log(formData);
  return (
    <form className="text-left">
      <div className="form-group">
        <label htmlFor="id">ID</label>
        <input
          type="number"
          className="form-control"
          name="id"
          disabled
          value={formData.id}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="firstName">First Name</label>
        <input
          type="text"
          className="form-control"
          name="firstName"
          value={formData.firstName}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="lastName">Last Name</label>
        <input
          type="text"
          className="form-control"
          name="lastName"
          value={formData.lastName}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="address">Address</label>
        <input
          type="text"
          className="form-control"
          name="address"
          value={formData.address}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="gender">Gender</label>
        <input
          type="text"
          className="form-control"
          name="gender"
          value={formData.gender}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="maritalStatus">Marital Status</label>
        <input
          type="text"
          className="form-control"
          name="maritalStatus"
          value={formData.marital_status}
          onChange={onChange}
        />
      </div>
      <input type="submit" className="btn btn-success btn-md" />
      <Link to="/" className="btn btn-danger btn-md">
        Cancel
      </Link>
    </form>
  );
};

export default CustomerForm;
