import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";
import store from "../../store";
import { getCustomerInfo } from "../../redux/actions/customer";

const Policy = ({ policyList }) => {
  useEffect(() => {
    store.dispatch(getCustomerInfo());
  }, []);

  return (
    <ul className="list-group mt-4">
      {policyList.map((policy) => {
        return (
          <Fragment key={policy.policyNum}>
            <li className="list-group-item list-group-item-info">
              Policy Number
            </li>
            <li className="list-group-item">{policy.policyNum}</li>
          </Fragment>
        );
      })}
    </ul>
  );
};

const mapStateToProps = (state) => ({
  policyList: state.customer.policyList,
});

export default connect(mapStateToProps)(Policy);
