import React, { useState, Fragment, useEffect } from "react";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import { createProfile, getCurrentProfile } from "../../redux/actions/profile";

const EditProfile = ({
  profile,
  loading,
  createProfile,
  getCurrentProfile,
  history,
}) => {
  const [formData, setFormData] = useState({
    birthday: undefined,
    address: undefined,
  });

  useEffect(() => {
    getCurrentProfile();
    setFormData({
      ...formData,
      birthday: !profile.birthday ? undefined : profile.birthday,
      address: !profile.address ? undefined : profile.address,
    });
  }, [loading]);

  const onChange = (event) => {
    const val = event.target.val;
    setFormData({ ...formData, [event.target.name]: val });
  };

  const onSubmit = (event) => {
    event.preventDefault();
    createProfile(formData, history);
  };
  return (
    <Fragment>
      <h1 className="large text-primary">Edit Your Profile</h1>
      <small>* = required field</small>
      <form className="form" onSubmit={onSubmit}>
        <div className="form-group">
          <input
            type="text"
            placeholder="birthday"
            name="birthday"
            required
            value={formData.birthday}
            onChange={onChange}
          />
          <small className="form-text">
            Type your birthday in format of MM/DD/YYYY
          </small>
        </div>
        <div className="form-group">
          <input
            type="text"
            placeholder="address"
            name="address"
            required
            value={formData.address}
            onChange={onChange}
          />
        </div>

        <input type="submit" className="btn btn-primary my-1" />
        <Link className="btn btn-light my-1" to="/dashboard">
          Go Back
        </Link>
      </form>
    </Fragment>
  );
};

const mapStateToProps = (state) => ({
  profile: state.profile,
  loading: state.profile.loading,
});

export default connect(mapStateToProps, { createProfile, getCurrentProfile })(
  withRouter(EditProfile)
);
