import { SET_ALERT, REMOVE_ALERT } from "../types";

const initialState = [];

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case SET_ALERT:
      return [...state, payload];

    default:
      return state;
  }
}
