import React, { Fragment } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";

const EmployeeDashBoard = ({ employee: { employee, customers } }) => {
  if (!employee) {
    return <Redirect to="/employee-login" />;
  }
  return (
    <Fragment>
      <h3>Your Clients</h3>
      {customers.map((customerCard) => {
        return (
          <div className="card">
            <div className="card-body">{customerCard.firstName}</div>
          </div>
        );
      })}
    </Fragment>
  );
};

const mapStateToProps = (state) => ({
  employee: state.employee,
});

export default connect(mapStateToProps)(EmployeeDashBoard);
